var pttrnAccordion=function(){var s=document.getElementsByClassName("accordion-tab");if(null!==s)for(var a=0;a<s.length;a++)s[a].addEventListener("click",function(){if(this.classList.contains("accordion-tab-closed")){for(var s=document.getElementsByClassName("accordion-tab-open"),a=0;a<s.length;a++)s[a].classList.add("accordion-tab-closed"),s[a].classList.remove("accordion-tab-open");this.classList.remove("accordion-tab-closed"),this.classList.add("accordion-tab-open")}else this.classList.remove("accordion-tab-open"),this.classList.add("accordion-tab-closed")})};